mapper.select:
select
, dept_name
from medical_project
join healthsystem.department d on medical_project.project_dept_id = d.dept_id


    and project_is_deleted = #{projectIsDeleted,jdbcType=BOOLEAN}


    and project_name like concat('%', #{projectName,jdbcType=VARCHAR},'%')


    and project_dept_id = #{projectDeptId}


    and project_price >= #{beginPrice,jdbcType=DECIMAL}


    and project_price <= #{endPrice,jdbcType=DECIMAL}
